(function($) {
	
	$.fn.serializeJson=function(){
		var serializeObj={};
		$(this.serializeArray()).each(function(){
			serializeObj[this.name]=this.value;
		});
		return serializeObj;
	};
	
	var txt = "";
	var uni = "";

	$.fn.extend({
		combobox : function(options) {
			var sel = this;
			var _width = 0;
			var id = sel.attr("id");
			var div = $("#"+id).parent('.seldiv');
			_width = div.width();
			var array = new Array();
			$("#"+id+" option").each(function() {
				var obj = {};
				obj.value=$(this).val();
				obj.text=$(this).text();
				array.push(obj);
			});
			div.empty();
			var dl = $("<dl></dl>");
			var dt = $("<dt></dt>");
			var cont = "";
			var contId= "";
			var dd = $("<dd id='"+id+"_suggest'></dd>");
			var i = 0;
			var _wordage = _width==0?6:Math.floor((_width-36)/14);
			$.each(array, function(n, obj){
				if(options!=undefined) {
					if(options.content == obj.value) {
						cont = obj.text;
						contId = obj.value;
					}
				}
				var overall=limit = obj.text;
				if(overall.gblen()>_wordage)
					limit = overall.substring(0, _wordage) + "..."; 
				var a = $("<a href='javascript:void(0);' onclick='fillInput("+id+"_suggest,this,"+id+"_text,"+id+"_val)'><span value='"+obj.value+"' overall='"+overall+"'>"+limit+"</span></a>");
				dd.append(a);
				i++;
			});
			if(i>8) {
				$(dd).css("height","200px");
			} else {
				$(dd).css("height",21*i+"px");
			}
			var input = $("<input type='text' id='"+id+"_text' value='"+cont+"' />");
			dd.css("width", div.width()+"px");
			$(dd).on("mouseleave", function(event) {
				if($("#"+id+"_text").val()=="") {
					$("#"+id+"_val").val("");
				}
				x = event.toElement;
				if(!$(x).is("dd")) {
					$("#"+id+"_suggest").css("display", "none");
				}
			});
			dt.append(input);
			dl.append(dt);
			dl.append(dd);
			div.append(dl);
			div.css("display", "block");
			var inp = $("<input type='text' name='"+id+"' id='"+id+"_val' style='display:none;' value='"+contId+"' />");
			div.append(inp);
			
			$("#"+id+"_text").on("mouseenter", function() {
				$("#"+id+"_suggest").css("display", "block");
				$("#"+id+"_suggest").css("height","200px");
				$("#"+id+"_suggest a").css("display", "block");
				//兼容ie和火狐浏览器的方式，但是经测试发现ie678可以ie9却不行在删除的时候无法触发，网上查下说有ie9这个问题
				var input = document.getElementById(id+"_text");
				if(getExplorer()=="ie") {
					input.onpropertychange = onchange;
				} else {
					input.oninput = onchange;
				}
			}).on("mouseleave", function(event) {
				if($("#"+id+"_text").val()=="") {
					$("#"+id+"_val").val("");
				}
				x = event.toElement;
				if($(x).is("dd") || $(x).is("dt") || $(x).is("a")) {
					return;
				} else {
					$("#"+id+"_suggest").css("display", "none");
				}
			});
				
			function onchange() {
				$("#"+id+"_suggest").css("display", "block");
				txt = this.value;
				uni = txt.charCodeAt(0);


				var n = 0;//计算一共有多少数据被过滤出来
				var _m = $("#"+id+"_suggest a").length;
				var _l = 0, _h = 0;
				if(_m>1000) {
					step = Math.ceil(_m/1000);
					for(var t = 0;t<=step;t++) {
						_l = t==0?0:t*1000 + 1;
						_h = (t+1)*1000>_m?((t+1)*1000-((t+1)*1000-_m)):(t+1)*1000;
						n = doit(_l, _h, n);
					}
				} else {
					n = doit(_l, _m, n);
				}
				
				if(n>8) {
					$("#"+id+"_suggest").css("height","200px");
				} else {
					$("#"+id+"_suggest").css("height",21*n+"px");
				}
			}
			
			function doit(_l, _h, n) {
				var _oa = eval($("#"+id+"_suggest a"));
				for(var k=_l;k<_h;k++) {
					var obj = _oa[k];
					var re = $(obj).find('span').html();
					if(re == "") {
						return n;
					}
					if(uni > 40869 || uni < 19968) {
						var unit = '';
						for (var i=0, len=txt.length; i<len; i++) {
							unit+=GetPY(re.charAt(i));
						}
						if(unit==txt) {
							$(obj).css("display", "block");
							n++;
						} else {
							$(obj).css("display", "none");
						}
						unit = '';
					} else {
						if(re.indexOf(txt)>=0) {
							$(obj).css("display", "block");
							n++;
						} else {
							$(obj).css("display", "none");
						}
					}
				}
				return n;
			}

			function fillInput(vid, va, vinp, vinpt) {
				$(vinp).val();
				$(vinp).val($(va).find('span').attr('overall'));
				$(vinpt).val($(va).find('span').attr('value'));
				$(vid).css("display", "none");
			}

			//返回汉字的拼音的第一个字母
			function GetPY(str) {
			    var ret = '';
			    for(var i=0,len=str.length;i<len;i++)
			    {
			        var ch = str.charAt(i);
			        ret += CheckCh(ch);
			    }
			    
			    return ret;
			}

			function CheckCh(ch) {
			    var uni = ch.charCodeAt(0);
			    var strChineseFirstPY = '';
			    //如果不在汉字处理范围之内,返回原字符
			    if(uni > 40869 || uni < 19968)
			        return ch; 
			    return strChineseFirstPY.charAt(uni-19968);
			}			
		}
	});
})(jQuery);

function Scrollbar() {
	this.options = {
		total : 0, //数据总数 
		pos : 0, //当前滚动位置 
		itemSize : 20, //单项尺寸 
		size : 200
	//控件尺寸 
	};
}
Scrollbar.prototype = (function() {
	function setOptions(options) {
		for ( var attr in options) {
			this.options[attr] = options[attr];
		}
		Refresh(this);
	}
	function Refresh(_this) {
		if (!_this.created)
			return; //设置控件高度 
		_this.bar.style.height = _this.options.size + "px"; //设置内容高度 
		var ch = _this.options.total * _this.options.itemSize;
		_this.content.style.height = ch + "px";
	} //获取滚动位置 
	function getPos() {
		var top = this.bar.scrollTop;
		var pos = parseInt(top / this.options.itemSize);
		return pos;
	} //每页可展示的数据数量 
	function getPageItems() {
		return this.options.size / this.options.itemSize;
	} //滚动事件响应 
	function OnScroll(_this) {
		var pos = _this.getPos();
		if (pos == _this.options.pos)
			return;
		_this.options.pos = pos;
		_this.onScroll(pos);
	} //滚动条创建 
	function CreateAt(dom) {
		var _this = this;
		var bar = document.createElement_x("div");
		var content = document.createElement_x("div");
		bar.appendChild(content);
		bar.style.width = "19px";
		bar.style.overflowY = "scroll";
		bar.style.overflowX = "hidden";
		if (bar.attachEvent) {
			bar.attachEvent("onscroll", function() {
				OnScroll(_this);
			});
		} else {//firefox兼容 
			bar.addEventListener("scroll", function() {
				OnScroll(_this);
			}, false);
		}
		content.style.backgroundColor = "white";
		content.style.width = "1px";
		this.bar = bar;
		this.content = content;
		if (typeof (dom) == "string") {
			dom = document.getElementByIdx_x(dom);
		}
		dom.innerHTML = "";
		dom.appendChild(this.bar);
		this.created = true;
		Refresh(this);
	}
	return {
		setOptions : setOptions,
		CreateAt : CreateAt,
		getPos : getPos,
		getPageItems : getPageItems,
		onScroll : null
	//模拟滚动条事件 
	};
})();

String.prototype.gblen = function() {
  var len = 0;
  for (var i=0; i<this.length; i++) {
    if (this.charCodeAt(i)>127 || this.charCodeAt(i)==94) {
    	len ++;
    } else {
    	len += 0.5;
    }
  }
  return Math.floor(len);
}

function getExplorer() {
	var explorer = window.navigator.userAgent ;
	//ie浏览器
	if (explorer.indexOf("MSIE") >= 0) {
		return "ie";
	}
	//firefox 浏览器
	else if (explorer.indexOf("Firefox") >= 0) {
		return "ff";
	}
	//Chrome浏览器
	else if(explorer.indexOf("Chrome") >= 0){
		return "gg";
	}
	//Opera浏览器
	else if(explorer.indexOf("Opera") >= 0){
		return "op";
	}
	//Safari浏览器
	else if(explorer.indexOf("Safari") >= 0){
		return "sa";
	}
}

